mapper.select:
select dcdg_basic_coupon_base.COUPON_LOGO, dcdg_basic_coupon_base.COUPON_ID AS COUPON_ID, dcdg_basic_coupon_base.COUPON_NAME, dcdg_basic_coupon_base.COUPON_CONENT, dcdg_basic_coupon_base.COUPON_WAY, dcdg_basic_coupon_base.EXPLAIN, a.IS_USE AS IS_USE, a.COUPON_IMG, dcdg_basic_coupon_base.COUPON_TYPE, dcdg_basic_coupon_base.START_TIME, dcdg_basic_coupon_base.DIE_TIME from (select COUPON_ID,IS_USE,COUPON_IMG from dcdg_bussiness_customer_coupon where CUST_ID = #{custId,jdbcType=CHAR}) as a RIGHT JOIN dcdg_basic_coupon_base ON dcdg_basic_coupon_base.COUPON_ID = a.COUPON_ID
where IS_USE=2
and COUPON_WAY = #{couponWay,jdbcType=TINYINT}

mapper.update:
update  dcdg_bussiness_customer_coupon set IS_USE=1
 ,GAIN_ID = #{gainId,jdbcType=CHAR}
 , USER_TIME= now()
 where
 IS_USE=2 and COUPON_ID=#{couponId,jdbcType=CHAR}

mapper.select:
select  a.COUPON_ID ,a.COUPON_WAY,a.ADD_TIME  from dcdg_basic_coupon_base a
  left join dcdg_bussiness_customer_coupon b  on a.COUPON_ID=b.COUPON_ID
where a.DIE_TIME > now()
and a.HAVE_TYPE=2
  and HAVE_NUM > (
  select  count(1) from dcdg_bussiness_customer_coupon  where dcdg_bussiness_customer_coupon.COUPON_ID =a.COUPON_ID
  )

    and a.COUPON_TYPE = #{couponType,jdbcType=TINYINT}



    and a.COUPON_CONENT like concat('%',  #{couponConent,jdbcType=VARCHAR},'%')


      and b.COUPON_ID = #{couponId,jdbcType=CHAR}


    and b.CUST_ID = #{custId,jdbcType=CHAR}

select COUPON_ID,COUPON_NAME,COUPON_LOGO,COUPON_TYPE,COUPON_STYLE,HAVE_TYPE,SEND_COUNT,COUPON_MONEY_TYPE ,COUPON_MONEY,MIN_MONEY,ADD_TIME,ADD_USER,START_TIME,DIE_TIME,COUPON_WAY from dcdg_basic_coupon_base where ADD_USER = #{addUser,jdbcType=CHAR}
and DIE_TIME > now()
and DIE_TIME < now()
