mapper.insert:

insert into dcdg_business_settlement_info (SETTLE_ID, MONEY, MONEY_TYPE, 
  SHOP_ID, SETTLEMENT_TYPE, SETTLEMENT_STATUS, 
  SHOP_NAME, ADD_TIME, IN_SETTLEMENT_TIME, 
  OUT_SETTLEMENT_TIME, BANK_CARD_ID, ALL_ORDER_ID
  )
values (#{settleId,jdbcType=CHAR}, #{money,jdbcType=DOUBLE}, #{moneyType,jdbcType=TINYINT}, 
  #{shopId,jdbcType=CHAR}, #{settlementType,jdbcType=TINYINT}, #{settlementStatus,jdbcType=TINYINT}, 
  #{shopName,jdbcType=VARCHAR}, #{addTime,jdbcType=TIMESTAMP}, #{inSettlementTime,jdbcType=TIMESTAMP}, 
  #{outSettlementTime,jdbcType=TIMESTAMP}, #{bankCardId,jdbcType=CHAR}, #{allOrderId,jdbcType=LONGVARCHAR}
  )

mapper.select:
select SETTLE_ID,MONEY,MONEY_TYPE,IN_SETTLEMENT_TIME,OUT_SETTLEMENT_TIME,SETTLEMENT_STATUS,1 dataType from dcdg_business_settlement_info
where             SHOP_ID = #{shopId,jdbcType=CHAR}

  and  IN_SETTLEMENT_TIME >= #{monthBegin}


  and IN_SETTLEMENT_TIME <= #{monthEnd}
