mapper.select:

select OPT_ID, SPEC_ID, OPT_NAME, OPT_DESC, OTHER_VAL, IS_SEARCH, GOODS_COUNT, ADD_TIME
from dcdg_goods_specification_option
where OPT_ID = #{optId,jdbcType=CHAR}

select OPT_NAME
from dcdg_goods_specification_option
  where OPT_ID = #{optId,jdbcType=CHAR}

select OPT_ID, SPEC_ID, OPT_NAME from dcdg_goods_specification_option where SPEC_ID in
(
  select SPEC_ID from  dcdg_goods_category_specification where CAT_ID=#{catId}
)
